import { useState } from 'react';

import { Dropdown, DropdownTrigger, DropdownMenu, 
    DropdownContent, DropdownItem, Icon, Button, DropdownDivider } from 'bloomer';

export default function DropOptions() {
    const [active, setActive] = useState(false);
    return (
        <Dropdown isAlign='left' isActive={active}>
            <DropdownTrigger>
                <Button aria-haspopup="true" aria-controls="dropdown-menu3" isColor='primary' onClick={() => setActive(true)}>
                    <Icon isSize='small' className='fa fa-user-edit' />
                    <span>Editar Professor</span>
                </Button>
            </DropdownTrigger>
            <DropdownMenu>
                <DropdownContent>
                    <DropdownItem>Editar</DropdownItem>
                    <DropdownDivider />
                    <DropdownItem>Excluir</DropdownItem>
                    <DropdownDivider />
                    <DropdownItem>Ver mais detalhes</DropdownItem>
                </DropdownContent>
            </DropdownMenu>
        </Dropdown>
    )
}
