import { Modal, ModalBackground, ModalContent, ModalClose, Box } from 'bloomer';

export default function ModalContainer({ active, close, children, color}) {
    const style = { backgroundColor: '#00d1b2' }
    return (
        <Modal isActive={active}>
            <ModalBackground style={ color && style } onClick={close} />
            <ModalContent >
                <Box >
                    {children}
                </Box>
            </ModalContent>
            <ModalClose onClick={close} />
        </Modal>
    )
}
