import { Menu, MenuLabel, MenuList, MenuLink } from 'bloomer';
import { useRouter } from 'next/router';
import Link from 'next/link';
import { routes } from './routes';

export default function Sidebar() {
    const router = useRouter();

    function isActive(path) {
        if (path === router.pathname) {
            return true;
        } else {
            return false
        }
    }

    return (
        <Menu>
            <MenuLabel>Geral</MenuLabel>
            <MenuList>
                {routes.geral.map(({ key, path, name}) => (
                        <MenuLink key={key} isActive={isActive(path)}>
                            <Link href={path}><a>{name}</a></Link>
                        </MenuLink>
                    )
                )}
            </MenuList>
            <MenuLabel>Administração</MenuLabel>
            <MenuList>
                <MenuLink>Financeiro</MenuLink>
                <MenuLink>Histórico de Saúde</MenuLink>
                <MenuLink>Alertas</MenuLink>
                <MenuLink>Relatórios</MenuLink>
            </MenuList>
            <MenuLabel>Configurações</MenuLabel>
            <MenuList>
                <MenuLink>Meu perfil</MenuLink>
                <MenuLink>Minha conta</MenuLink>
                <MenuLink>Ajuda</MenuLink>
            </MenuList>
        </Menu>
    );
}
