    return (
        <Container>
            <Image style={styles.logo} src='/static/img/logo-white.png' />
            <Title>Recuperar Senha</Title>
            <Subtitle>Entre seu e-mail abaixo e enviaremos um link de recuperação de senha</Subtitle>
