import { Modal, ModalBackground, ModalContent, 
    ModalClose, Box, ModalCard, ModalCardBody,
    ModalCardHeader, ModalCardTitle, ModalCardFooter
} from 'bloomer';

export default function ModalContainer({ active, close, children, 
    colored, isCard, title }) {
    const style = { 
        color: {
            backgroundColor: '#00d1b2' 
        },
        size: {
            width: '60vw'
        }
    }

    if (isCard) {
        return (
            <Modal>
                <ModalBackground />
                <ModalCard>
                    <ModalCardHeader>
                        <ModalCardTitle>{title}</ModalCardTitle>
                        <Delete />
                    </ModalCardHeader>
                    <ModalCardBody>
                        {children}
                    </ModalCardBody>
                    <ModalCardFooter>
                        <Button isColor='success'>Sim</Button>
                        <Button isColor='warning'>Cancelar</Button>
                    </ModalCardFooter>
                </ModalCard>
            </Modal>
        );
    }

    return (
        <Modal isActive={active}>
            <ModalBackground style={ colored && style.color } onClick={close} />
            <ModalContent style={style.size}>
                <Box >
                    {children}
                </Box>
            </ModalContent>
            <ModalClose onClick={close} />
        </Modal>
    )
}
