import { Menu, MenuLabel, MenuList, MenuLink } from 'bloomer';
import { useRouter } from 'next/router';
import Link from 'next/link';
import { routes } from './routes';

export default function Sidebar() {
    const { geral, admin, config } = routes;
    const router = useRouter();

    function isActive(path) {
        if (path === router.pathname) {
            return true;
        } else {
            return false
        }
    }

    function mapRoutes(route) {
        return route.map(({ key, path, name}) => (
            <MenuLink key={key} isActive={isActive(path)}><Link href={path}>{name}</Link></MenuLink>
        ));
    }

    return (
        <Menu>
            <MenuLabel>Geral</MenuLabel>
            <MenuList>
                {mapRoutes(geral)}
            </MenuList>
            <MenuLabel>Administração</MenuLabel>
            <MenuList>
                {mapRoutes(admin)}
            </MenuList>
            <MenuLabel>Configurações</MenuLabel>
            <MenuList>
                {mapRoutes(config)}
            </MenuList>
        </Menu>
    );
}
